```python

# packages/mcp/fluxloop_mcp/tools/detect_frameworks.py

def detect_frameworks(repository_profile: dict) -> dict:
    """
    Args:
        repository_profile: {"languages": ["python"], "entryPoints": ["app/main.py"]}
    """
    frameworks = analyze_code(repository_profile)

    # 각 프레임워크에 적합한 러너 패턴 매핑
    pattern_recommendations = []
    for fw in frameworks:
        if fw["name"] == "fastapi":
            pattern_recommendations.append({
                "framework": "fastapi",
                "runner_pattern": "python-function",

            "doc_url": "docs-cli/configuration/runners/python-function",
            "confidence": fw["confidence"]
        })
    elif fw["name"] == "openai":
        pattern_recommendations.append({
            "framework": "openai",
            "runner_pattern": "python-async-generator",
            "doc_url": "docs-cli/configuration/runners/python-async-generator",
            "confidence": 0.95
        })

return {
    "frameworks": frameworks,
    "recommended_patterns": pattern_recommendations

